import React from "react";
import { message, Row, Layout, Button, Dropdown, Icon, Menu } from "antd";
import routes from "../../../routes";
import './AuthLayout.css'
import SideMenu from "./SideMenu";

const { Header, Content } = Layout;

class AuthLayout extends React.Component {
  componentDidMount() {
    if (!localStorage.getItem("token")) {
      // User is not logged in. Redirect back to login
      this.props.history.push(routes.login);
      message.warning("Please login first");
      return;
    }
    // Fetch data for logged in user using token
  }

  onLogout = () => {
    // Remove token & other stored data
    localStorage.clear();
    this.props.history.push(routes.login);
  };

  render() {
    return (
      <Layout className={"main-layout"}>
        <Header
          style={{
            position: "fixed",
            zIndex: 1,
            width: "100%",
            backgroundColor: "#282828"
          }}
        >

          <Row
            type="flex"
            align="middle"
            justify="end"
            style={{ height: "100%" }}
          >
            <Dropdown
              overlay={
                <Menu>
                  <Menu.Item key="1" onClick={this.onLogout}>
                    <Icon type="logout" />
                    Logout
                  </Menu.Item>
                </Menu>
              }
              trigger={["click"]}
            >
              <Button ghost className="primary-btn">
                user@gmail.com <Icon type="down" />
              </Button>
            </Dropdown>
          </Row>
        </Header>
          <div className={"row"}>
              <div className={"col s4"}>
                  <SideMenu />
                  <Content style={{ padding: "0 0px", marginTop: 0, height: "100vh" }} className={"content"}>
                      {this.props.children}
                  </Content>
              </div>

          </div>

      </Layout>
    );
  }
}

export default AuthLayout;
