import React, {Component} from "react"
import { Card, Form, Input, Button } from "antd";
import routes from "../routes";
const handleSubmit = () => {
    // Set token into localstorage
    localStorage.setItem("token", "I am now logged in");
    props.history.push(routes.field);
};
export default class LoginPageV2 extends Component {
    render() {
        return (
            <Card
                bordered={false}
                style={{
                    border: "1px solid #282828",
                    boxShadow: "0px 15px 20px 5px #0000001a",
                    width: 400,
                    backgroundColor: "grey",
                }}
            >
                <h1 style={{ textAlign: "center" }}>Midnight Path Planning</h1>
                <Form
                    hideRequiredMark
                    colon={false}
                    onSubmit={handleSubmit}
                    layout="vertical"
                >
                    <Form.Item label="Email" rules={[{ required: true, message: "Email required" }]}>
                        <Input size="large" placeholder="John@example.com" />
                    </Form.Item>
                    <Form.Item label="Password" rules={[{ required: true, message: "Password required" }]}>
                        <Input.Password
                            size="large"
                            type="password"
                            placeholder="Password"
                        />
                    </Form.Item>
                    <Form.Item label="Robot Name">
                        <Input size="large" placeholder="Robot One" />
                    </Form.Item>
                    <Form.Item>
                        <Button block size="large" htmlType="submit">
                            Login
                        </Button>
                    </Form.Item>
                </Form>
            </Card>
        );
    }

}
