import React from "react";
import { Card, Form, Input, Button } from "antd";
import routes from "../routes";
import 'antd/dist/antd.css';
const LoginPage = props => {
    const layout = {
        labelCol: {
            span: 8,
        },
        wrapperCol: {
            span: 16,
        },
    };
    const tailLayout = {
        wrapperCol: {
            offset: 8,
            span: 16,
        },
    };
  const { getFieldDecorator } = props.form;
  const handleSubmit = () => {
    // Set token into localstorage
    localStorage.setItem("token", "I am now logged in");
    props.history.push(routes.field);
  };
    const onFinish = values => {
        console.log('Success:', values);
    };

    const onFinishFailed = errorInfo => {
        console.log('Failed:', errorInfo);
    };
  return (
    <>
      <Card
        bordered={false}
        style={{
          border: "1px solid #282828",
          boxShadow: "0px 15px 20px 5px #0000001a",
          width: 400,
          backgroundColor: "grey",
        }}
      >
        <h1 style={{ textAlign: "center" }}>Midnight Path Planning</h1>
          <Form
              {...layout}
              name="basic"
              initialValues={{
                  remember: true,
              }}
              onFinish={onFinish}
              onFinishFailed={onFinishFailed}
          >
              <Form.Item
                  label="Username"
                  name="username"
                  rules={[
                      {
                          required: true,
                          message: 'Please input your username!',
                      },
                  ]}
              >
                  <Input />
              </Form.Item>

              <Form.Item
                  label="Password"
                  name="password"
                  rules={[
                      {
                          required: true,
                          message: 'Please input your password!',
                      },
                  ]}
              >
                  <Input.Password />
              </Form.Item>



              <Form.Item {...tailLayout}>
                  <Button type="primary" htmlType="submit">
                      Submit
                  </Button>
              </Form.Item>
          </Form>
      </Card>
    </>
  );
};

export default Form.create({ name: "Login" })(LoginPage);
